def odefunction(t,y,const):    # Système d'équations différentielles ordinaires défini par :    #   t:  le temps    #   y: les variables du système    #  La fonction retourne dy, un array contenant les dérivées        #Import nécessaire     import numpy as np        #Partie principale    #Definitions de constantes    epsilon=const[0]    omega=const[1]    dy = np.zeros(2)    dy[0] = y[1]    dy[1] = epsilon * omega * (1-y[0]**2)*y[1] - (omega**2)*y[0]        return dy